package main

import (
  "fmt"
  "github.com/spf13/cobra"
)

func main() {
  rootCmd := &cobra.Command{
    Use:   "app",
    Short: "StackForge Cobra CLI",
    Run: func(cmd *cobra.Command, args []string) {
      fmt.Println("Hello from StackForge + Cobra")
    },
  }

  rootCmd.Execute()
}
